describe HaikusController do

  # This should return the minimal set of attributes required to create a valid
  # Haiku. As you add validations to Haiku, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all haikus as @haikus" do
      haiku = Haiku.create! valid_attributes
      get :index
      assigns(:haikus).should eq([haiku])
    end
  end

  describe "GET show" do
    it "assigns the requested haiku as @haiku" do
      haiku = Haiku.create! valid_attributes
      get :show, :id => haiku.id
      assigns(:haiku).should eq(haiku)
    end
  end

  describe "GET new" do
    it "assigns a new haiku as @haiku" do
      get :new
      assigns(:haiku).should be_a_new(Haiku)
    end
  end

  describe "GET edit" do
    it "assigns the requested haiku as @haiku" do
      haiku = Haiku.create! valid_attributes
      get :edit, :id => haiku.id
      assigns(:haiku).should eq(haiku)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Haiku" do
        expect {
          post :create, :haiku => valid_attributes
        }.to change(Haiku, :count).by(1)
      end

      it "assigns a newly created haiku as @haiku" do
        post :create, :haiku => valid_attributes
        assigns(:haiku).should be_a(Haiku)
