      end

      it "redirects to the created haiku" do
        post :create, :haiku => valid_attributes
        response.should redirect_to(Haiku.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved haiku as @haiku" do
        # Trigger the behavior that occurs when invalid params are submitted
        Haiku.any_instance.stub(:save).and_return(false)
        post :create, :haiku => {}
        assigns(:haiku).should be_a_new(Haiku)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Haiku.any_instance.stub(:save).and_return(false)
        post :create, :haiku => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested haiku" do
        haiku = Haiku.create! valid_attributes
        # Assuming there are no other haikus in the database, this
        # specifies that the Haiku created on the previous line
        # receives the :update_attributes message with whatever params are
