      end

      it "redirects to the haiku" do
        haiku = Haiku.create! valid_attributes
        put :update, :id => haiku.id, :haiku => valid_attributes
        response.should redirect_to(haiku)
      end
    end

    describe "with invalid params" do
      it "assigns the haiku as @haiku" do
        haiku = Haiku.create! valid_attributes
        # Trigger the behavior that occurs when invalid params are submitted
        Haiku.any_instance.stub(:save).and_return(false)
        put :update, :id => haiku.id, :haiku => {}
        assigns(:haiku).should eq(haiku)
      end

      it "re-renders the 'edit' template" do
        haiku = Haiku.create! valid_attributes
